from tkinter import *root = Tk()root.title("project 146")root.geometry("500x500")input1 = Entry(root)label_1 = Label(root , text = "Fibbonaci Series: ")label_2 = Label(root , text = "Fibbonaci Sum: ")def Fibbonaci():        num = int(input1.get())        no1 = 0        no2 = 1        no3 = 0        sum1 = 1        counter = 2        label_1["text"] = str(no1) + " " + str(no2) + " "        while (counter < num):                counter = counter + 1                   no3 = no1 + no2        no1 = no2        no2 = no3        sum1 = sum1 + no3        label_1["text"] += str(no3) + " "        label_2["text"] = str(sum1)                                     button = Button(root , text = "Show Fibonacci Seires" , command = Fibbonaci)input1.pack()button.pack()label_1.pack()label_2.pack()root.mainloop()